/** @format */

import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { listAllFarms } from '../actions/userActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import ProgressBar from '../components/ProgressBar';

function MyFarms(props) {
	const userAllFarms = useSelector((state) => state.userAllFarms);
	const { loading, farms, error } = userAllFarms;

	const dispatch = useDispatch();
	useEffect(() => {
		dispatch(listAllFarms());
		return () => {
			//
		};
	}, [dispatch]);

	return (
		<>
			<div className="container">
				{loading ? (
					<LoadingBox></LoadingBox>
				) : error ? (
					<MessageBox variant="danger">{error}</MessageBox>
				) : (
					<div className="mt-5">
						<div className="row">
							<div className="col-ml-12 pb-4">
								<h4 className="">My Farms</h4>
							</div>
						</div>

						<div className="product-list mt-4 mb-5 table-responsive">
							{farms.length > 0 ? (
								<table className="table text-nowrap">
									<thead>
										<tr>
											<th>Farm ID</th>
											<th>Farm Name </th>
											<th>Farm Size </th>
											<th>Farm Capacity</th>
											<th></th>
										</tr>
									</thead>
									<tbody>
										{farms.map((farm) => (
											<tr key={farm._id}>
												<td>{farm._id}</td>
												<td>{farm._id}</td>
												<td>{farm.farm}</td>
												<td>
													<Link to={'/farm/' + farm._id}>View farm</Link>
												</td>
											</tr>
										))}
									</tbody>
								</table>
							) : (
								<div className="center mt-5">Farm List is Empty, Add a New Farm.</div>
							)}
						</div>
					</div>
				)}
			</div>
		</>
	);
}

export default MyFarms;
