/** @format */

import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { register } from '../actions/userActions';
import MessageBox from '../components/MessageBox';
import LoadingBox from '../components/LoadingBox';

function UserLocation(props) {
	const [state, setState] = useState('');
	const [city, setCity] = useState('');
	const [cluster, setCluster] = useState('');
	const userRegister = useSelector((state) => state.userRegister);
	const { loading, userInfo, error } = userRegister;
	const dispatch = useDispatch();
	const redirect = '/';

	const submitHandler = (e) => {
		e.preventDefault();
		dispatch(register(state, city, cluster));
	};

	useEffect(() => {
		if (userInfo) {
			setTimeout(() => {
				props.history.push(redirect);
			}, 3000);
		}
		return () => {
			//
		};
	}, [userInfo, props.history]);

	return (
		<div className="container">
			{loading ? (
				<LoadingBox></LoadingBox>
			) : (
				<div className="row mt-5 mb-5">
					<div className="col-md-3"></div>
					<div className="col-md-6">
						<div className="container card ">
							<article className="card-body ">
								<div>
									{error && <MessageBox variant="danger">{error}</MessageBox>}
									{userInfo && (
										<MessageBox variant="success">
											Registration is Complete, Please Login.
										</MessageBox>
									)}
									<h4 className="card-title mt-3 text-center">Location</h4>
									<p className="text-center">Get started with your free account</p>
									<form onSubmit={submitHandler}>
										<div className="form-group input-group" id="name">
											<div className="input-group-prepend">
												<span className="input-group-text">
													<i className="fa fa-location"></i>
												</span>
											</div>
											<input
												name=""
												className="form-control"
												placeholder="State"
												type="text"
												onChange={(e) => setState(e.target.value)}
												required={true}
											/>
										</div>
										<div className="form-group input-group" id="name">
											<div className="input-group-prepend">
												<span className="input-group-text">
													<i className="fa fa-location"></i>
												</span>
											</div>
											<input
												name=""
												className="form-control"
												placeholder="City"
												type="text"
												onChange={(e) => setCity(e.target.value)}
												required={true}
											/>
										</div>
										<div className="form-group input-group" id="password">
											<div className="input-group-prepend">
												<span className="input-group-text">
													<i className="fa fa-group"></i>
												</span>
											</div>
											<input
												className="form-control"
												placeholder="Cluster you belong to?"
												type="password"
												onChange={(e) => setCluster(e.target.value)}
												required={true}
											/>
										</div>
										<div className="form-group">
											<button
												type="submit"
												className="btn btn-primary btn-block"
												onClick={submitHandler}
											>
												Submit
											</button>
										</div>
										<p className="text-center">
											Have an account? <Link to="/">Log in</Link>
										</p>
									</form>
								</div>
							</article>
						</div>
					</div>
					<div className="col-md-3"></div>
				</div>
			)}
		</div>
	);
}

export default UserLocation;
