/** @format */

import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { userFarmDetail } from '../actions/userActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import ProgressBar from '../components/ProgressBar';

function Package(props) {
	const userFarmDetails = useSelector((state) => state.userFarmDetails);
	const { loading, error, farm } = userFarmDetails;

	const dispatch = useDispatch();

	useEffect(() => {
		dispatch(userFarmDetail(props.match.params.id));
		return () => {
			//
		};
	}, [dispatch, props.match]);

	return (
		<>
			{loading ? (
				<LoadingBox></LoadingBox>
			) : error ? (
				<MessageBox>{error}</MessageBox>
			) : (
				<div className="container">
					<div className="mt-5">
						<div className="contain-box">
							<ul className="border-bottom">
								<li className="">
									<h5 className="">{farm.poultry_type}</h5>
								</li>
								<li className="verify-input">
									<div className="">
										<Link to="product_verify" className="btn btn-primary btn-sm">
											VERIFY INPUT
										</Link>
									</div>
								</li>
							</ul>
						</div>
						<div className="">
							<span className="today-date">
								Today's Date & Time: <span className=""> 20 - 04 - 2020 | 4:01 PM </span>
							</span>

							<p className="expected-points mt-3">Expected Points: 100</p>
							<p className="">Accrued Points: 0 Points </p>
							<ProgressBar striped completed={7} bgcolor="#00695c" />
						</div>
						<div className="row">
							<div className="col-md-6">
								<div className="info-container">
									<p className="">Date Subscribed: 15th April, 2020</p>
									<p className="">Stocking Date: 15th May, 2020</p>
									<p className="">Expected End of Cycle: 10th July, 2020</p>
									<p className="">Expected Av. Weight: 1.2Kg LW</p>
								</div>
							</div>
							<div className="col-md-6">
								<div className="info-contain">
									<p className="">Stocking Qty.: 51 Birds</p>
									<p className="">Mortality: 0 Birds</p>
									<p className="">Total: 51 Birds</p>
								</div>
							</div>
							<div className="col-md-12 mt-3 standard">
								<h4 className="">
									<i className=""></i> Expected Standared
								</h4>
								<p className=""> - Total Bags of Feed: 8 Bags</p>
								<p className=""> - Drugs & Vaccine: 0</p>
								<p className=""> - Other Activities: 0</p>
								<p className=""> - Farm Equipment: 0</p>
							</div>
						</div>
					</div>
				</div>
			)}
		</>
	);
}

export default Package;
