/** @format */

import React, { Component } from 'react';
// import { Form } from 'semantic-ui-react';
import { connect } from 'react-redux';

class PicUploadNoCrop extends Component {
	constructor() {
		super();
		this.state = {
			photoFile: null,
			photoUrl: null
		};
	}

	handleFile = (e) => {
		this.setState({ photoFile: e.target.files[0] });
		const file = e.target.files[0];
		const fileReader = new FileReader();
		fileReader.onloadend = () => {
			this.setState({ photoFile: file, photoUrl: fileReader.result });
		};
		if (file) {
			fileReader.readAsDataURL(file);
		}
	};

	handleSubmit = (e) => {
		e.preventDefault();
		const formData = new FormData();
		formData.append('user[id]', this.props.currentUser.id);
		formData.append('user[profile_pic]', this.state.photoFile);
		// addPhotoToUser is an ajax call to save the picture in database
		// addPhotoToUser(this.props.currentUser, formData);
	};

	render() {
		const preview = this.state.photoUrl ? <img src={this.state.photoUrl} alt="preview" /> : null;

		return (
			<form onSubmit={this.handleSubmit}>
				<input
					type="file"
					id="profile_pic"
					value={this.state.profile_pic}
					onChange={this.handleFile}
				/>
				<Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
					{loadingUpdate ? (
						<LoadingBox></LoadingBox>
					) : (
						<>
							<DialogTitle id="form-dialog-title">Edit Profile</DialogTitle>
							<DialogContent>
								<form onSubmit={submitHandler}>
									<div className="form-group row">
										<label className="col-lg-3 col-form-label form-control-label">
											First name
										</label>
										<div className="col-lg-9">
											<input
												className="form-control"
												value={firstName}
												type="firstname"
												name="firstname"
												id="first_name"
												onChange={(e) => setFirstName(e.target.value)}
											/>
										</div>
									</div>
									<div className="form-group row">
										<label className="col-lg-3 col-form-label form-control-label">
											Last name
										</label>
										<div className="col-lg-9">
											<input
												className="form-control"
												value={lastName}
												type="lastname"
												name="lastname"
												id="last_name"
												onChange={(e) => setLastName(e.target.value)}
											/>
										</div>
									</div>
									<div className="form-group row">
										<label className="col-lg-3 col-form-label form-control-label">
											Phone
										</label>
										<div className="col-lg-9">
											<input
												className="form-control"
												value={phone}
												type="phone"
												name="phone"
												id="phone"
												onChange={(e) => setPhone(e.target.value)}
											/>
										</div>
									</div>
									<div className="form-group row">
										<label className="col-lg-3 col-form-label form-control-label">
											Address
										</label>
										<div className="col-lg-9">
											<input
												className="form-control"
												type="text"
												value={address}
												placeholder="Address"
												onChange={(e) => setAddress(e.target.value)}
											/>
										</div>
									</div>
									<div className="form-group row">
										<label className="col-lg-3 col-form-label form-control-label"></label>
										<div className="col-lg-5 mt-2">
											<input
												className="form-control"
												type="text"
												value={state}
												placeholder="State"
												onChange={(e) => setState(e.target.value)}
											/>
										</div>
										<div className="col-lg-4 mt-2">
											<input
												className="form-control"
												type="text"
												value={city}
												placeholder="City"
												onChange={(e) => setCity(e.target.value)}
											/>
										</div>
									</div>
									<div className="form-group row">
										<label className="col-lg-3 col-form-label form-control-label">
											Cluster
										</label>
										<div className="col-lg-9">
											<input
												className="form-control"
												type="Cluster"
												id="Cluster"
												name="Cluster"
												value={cluster}
												onChange={(e) => setCluster(e.target.value)}
											></input>
										</div>
									</div>

									<DialogActions>
										<Button onClick={handleClose} color="primary">
											Cancel
										</Button>
										<Button type="submit" color="primary">
											Update
										</Button>
									</DialogActions>
								</form>
							</DialogContent>
						</>
					)}
				</Dialog>
				{preview}
				<button>save</button>
			</form>
		);
	}
}

const mapSTP = (state) => {
	return { currentUser: state.currentUser };
};

export default connect(mapSTP)(PicUploadNoCrop);
