/** @format */

import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { register } from '../actions/userActions';
import MessageBox from '../components/MessageBox';
import LoadingBox from '../components/LoadingBox';
import { USER_SAVE_REGISTER_RESET } from '../constants/userConstants';
import RegisterSteps from '../components/RegisterSteps';

function RegisterUserDetails(props) {
	const [firstName, setFirstName] = useState('');
	const [lastName, setLastName] = useState('');
	const userRegister = useSelector((state) => state.userRegister);
	const { loading, userInfo, error } = userRegister;
	const dispatch = useDispatch();

	const submitHandler = (e) => {
		e.preventDefault();
		dispatch(register({ firstName, lastName }));
		props.history.push('/location');
		dispatch({ type: USER_SAVE_REGISTER_RESET });
	};

	return (
		<div className="container">
			<RegisterSteps step1 step2></RegisterSteps>
			{loading ? (
				<LoadingBox></LoadingBox>
			) : (
				<div className="row mt-5 mb-5">
					<div className="col-md-1"></div>
					<div className="col-md-6">
						<div className="container card ">
							<article className="">
								<div>
									{error && <MessageBox variant="danger">{error}</MessageBox>}
									{userInfo && (
										<MessageBox variant="success">
											Registration is successful. Fill in Your Location
										</MessageBox>
									)}
									<h4 className="card-title mt-3 text-center">Create Account</h4>
									<p className="text-center">Get started with your free account</p>
									<form onSubmit={submitHandler}>
										<div className="form-group input-group" id="name">
											<div className="input-group-prepend">
												<span className="input-group-text">
													<i className="fa fa-user"></i>
												</span>
											</div>
											<input
												name=""
												className="form-control"
												placeholder="First Name"
												type="text"
												onChange={(e) => setFirstName(e.target.value)}
												required={true}
											/>
										</div>
										<div className="form-group input-group" id="name">
											<div className="input-group-prepend">
												<span className="input-group-text">
													<i className="fa fa-user"></i>
												</span>
											</div>
											<input
												name=""
												className="form-control"
												placeholder="Last Name"
												type="text"
												onChange={(e) => setLastName(e.target.value)}
												required={true}
											/>
										</div>
										<div className="form-group">
											<button
												type="submit"
												className="btn btn-primary btn-block"
												onClick={submitHandler}
											>
												Continue
											</button>
										</div>
										<p className="text-center">
											Have an account? <Link to="/">Log in</Link>
										</p>
									</form>
								</div>
							</article>
						</div>
					</div>
					<div className="col-md-1"></div>
				</div>
			)}
		</div>
	);
}

export default RegisterUserDetails;
