/** @format */

import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { resetPassword } from '../actions/userActions';

function ResetPassword(props) {
	const phone = props.
	const [password, setPassword] = useState('');
	const [confirmPassword, setConfirmPassword] = useState('');

	// const userCodeVerify = useSelector((state) => state.userCodeVerify);
	// const { loading, error, codeVerify } = userCodeVerify;
	const dispatch = useDispatch();

	const isValid = () => {
		if (password !== confirmPassword) {
			// throw error.message;
		}
	};
	const submitHandler = (e) => {
		e.preventDefault();
		dispatch(resetPassword(password, phone));
	};

	return (
		<>
			<div className="container">
				<div className="row">
					<div className="col-md-6">
						<div className="row">
							<div className="col-md-1"></div>
							<div className="col-md-10">
								<div className="passcontainer mt-5">
									<h4 className="password-test">Reset your password</h4>
									<p className="password-para">Change your password</p>
									<form className="" onSubmit={submitHandler}>
										<div className="form-group">
											<label htmlFor="exampleInputPassword1">Password</label>
											<input type="password" className="form-control" id="exampleInputPhone" placeholder="New password" onChange={(e) => setPassword(e.target.value)} required />
										</div>
										<div className="form-group">
											<label htmlFor="exampleInputPassword1">Confirm Password</label>
											<input
												type="password"
												className="form-control"
												id="exampleInputPhone"
												placeholder="Confirm new password"
												onChange={(e) => setConfirmPassword(e.target.value)}
												required
											/>
										</div>
										<button type="submmit" className="btn btn-success btn-block">
											Reset Password
										</button>
									</form>
								</div>
							</div>
							<div className="col-md-1"></div>
						</div>
					</div>
					<div className="col-md-6">
						<img src="/forgot-password-login-illustration.jpg" alt="picture" className="Image" />
					</div>
				</div>
			</div>
		</>
	);
}

export default ResetPassword;
