/** @format */

import React, { useState, useCallback, useRef, useEffect } from 'react';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';

function CropImage() {
	const [upImg, setUpImg] = useState();
	const imgRef = useRef(null);
	const previewCanvasRef = useRef(null);
	const [crop, setCrop] = useState({ unit: '%', width: 30, aspect: 16 / 9 });
	const [completedCrop, setCompletedCrop] = useState(null);

	const onSelectFile = (e) => {
		if (e.target.files && e.target.files.length > 0) {
			const reader = new FileReader();
			reader.addEventListener('load', () => setUpImg(reader.result));
			reader.readAsDataURL(e.target.files[0]);
		}
	};

	const onLoad = useCallback((img) => {
		imgRef.current = img;
	}, []);

	useEffect(() => {
		if (!completedCrop || !previewCanvasRef.current || !imgRef.current) {
			return;
		}

		const image = imgRef.current;
		const canvas = previewCanvasRef.current;
		const crop = completedCrop;

		const scaleX = image.naturalWidth / image.width;
		const scaleY = image.naturalHeight / image.height;
		const ctx = canvas.getContext('2d');
		const pixelRatio = window.devicePixelRatio;

		canvas.width = crop.width * pixelRatio;
		canvas.height = crop.height * pixelRatio;

		ctx.setTransform(pixelRatio, 0, 0, pixelRatio, 0, 0);
		ctx.imageSmoothingQuality = 'high';

		ctx.drawImage(
			image,
			crop.x * scaleX,
			crop.y * scaleY,
			crop.width * scaleX,
			crop.height * scaleY,
			0,
			0,
			crop.width,
			crop.height
		);
	}, [completedCrop]);

	return (
		<div className="App">
			<div>
				<input type="file" accept="image/*" onChange={onSelectFile} />
			</div>
			<form onSubmit={this.handleSubmit}>
				<label htmlFor="profile_pic"></label>
				<input
					type="file"
					id="profile_pic"
					value={profile_pic}
					onChange={this.handleFile}
					className="btn btn-success"
				/>
				{src && (
					<Dialog open aria-labelledby="form-dialog-title">
						<DialogContent>
							<ReactCrop
								src={src}
								crop={crop}
								onImageLoaded={this.onImageLoaded}
								onComplete={this.onCropComplete}
								onChange={this.onCropChange}
							/>
						</DialogContent>
						<button
							type="submit"
							onClick={this.handleSubmit}
							className="btn btn-success"
							color="primary"
						>
							Update
						</button>
					</Dialog>
				)}
			</form>
			<ReactCrop
				src={upImg}
				onImageLoaded={onLoad}
				crop={crop}
				onChange={(c) => setCrop(c)}
				onComplete={(c) => setCompletedCrop(c)}
			/>
			{/* <div>
        <canvas
          ref={previewCanvasRef}
          // Rounding is important so the canvas width and height matches/is a multiple for sharpness.
          style={{
            width: Math.round(completedCrop?.width ?? 0),
            height: Math.round(completedCrop?.height ?? 0)
          }}
        />
      </div> */}
			{/* <p>
				Note that the download below won't work in this sandbox due to the iframe missing
				'allow-downloads'. It's just for your reference.
			</p>
			<button
				type="button"
				disabled={!completedCrop?.width || !completedCrop?.height}
				onClick={() => generateDownload(previewCanvasRef.current, completedCrop)}
			>
				Download cropped image
			</button> */}
		</div>
	);
}
export default CropImage;
