/** @format */

import React, { useEffect } from 'react';
import { Line, Doughnut, Bar } from 'react-chartjs-2';
import { useDispatch, useSelector } from 'react-redux';
import { dashboardForAdmin } from '../actions/adminActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';

function AdminDashboard(props) {
	const adminDashBoardInfo = useSelector((state) => state.adminDashBoardInfo);
	const { loading, error, dashboardInfo } = adminDashBoardInfo;

	const dispatch = useDispatch();
	useEffect(() => {
		dispatch(dashboardForAdmin());
		return () => {
			//
		};
	}, [dispatch]);

	const data = {
		labels: ['1', '2', '3', '4', '5', '6'],
		datasets: [
			{
				label: '# of Votes',
				data: [12, 19, 3, 5, 2, 3],
				fill: false,
				backgroundColor: 'rgb(255, 99, 132)',
				borderColor: 'rgba(255, 99, 132, 0.2)'
			}
		]
	};

	// const data2 = {
	// 	labels: ['Used Products', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
	// 	datasets: [
	// 		{
	// 			label: '# of Votes',
	// 			data: [12, 19, 3, 5, 2, 3],
	// 			backgroundColor: [
	// 				'rgba(255, 99, 132, 0.2)',
	// 				'rgba(54, 162, 235, 0.2)',
	// 				'rgba(255, 206, 86, 0.2)',
	// 				'rgba(75, 192, 192, 0.2)',
	// 				'rgba(153, 102, 255, 0.2)',
	// 				'rgba(255, 159, 64, 0.2)'
	// 			],
	// 			borderColor: [
	// 				'rgba(255, 99, 132, 1)',
	// 				'rgba(54, 162, 235, 1)',
	// 				'rgba(255, 206, 86, 1)',
	// 				'rgba(75, 192, 192, 1)',
	// 				'rgba(153, 102, 255, 1)',
	// 				'rgba(255, 159, 64, 1)'
	// 			],
	// 			borderWidth: 1
	// 		}
	// 	]
	// };

	const options = {
		scales: {
			yAxes: [
				{
					ticks: {
						beginAtZero: true
					}
				}
			]
		}
	};

	return (
		<div className="">
			{loading ? (
				<LoadingBox></LoadingBox>
			) : error ? (
				<MessageBox variant="danger">{error}</MessageBox>
			) : (
				<div className="container-fluid">
					<div className="row">
						<div className="col-md-3">
							<p className="text-box">
								Number of Registered Users
								<h3 className="mt-3">{dashboardInfo.registeredUsers}</h3>
							</p>
						</div>
						<div className="col-md-3">
							<p className="text-box">
								Number of Registered Users Today{' '}
								<h3 className="mt-3">{dashboardInfo.registeredUsersToday}</h3>
							</p>
						</div>
						<div className="col-md-3">
							<p className="text-box">
								Number of Unused Products <h3 className="mt-3">{dashboardInfo.products}</h3>
							</p>
						</div>
						<div className="col-md-3">
							<p className="text-box">
								Number of Used Products <h3 className="mt-3">{dashboardInfo.usedPin}</h3>
							</p>
						</div>

						<div className="col-md-6">
							<p className="col-box">
								Number of Registered Farm{' '}
								<h3 className="mt-3">{dashboardInfo.registeredFarms}</h3>
							</p>
						</div>
						<div className="col-md-6">
							<p className="col-box">
								Number of Registered Farms Today{' '}
								<h3 className="mt-3">{dashboardInfo.registeredFarmsToday}</h3>
							</p>
						</div>
					</div>

					<div className="col-md-12 mb-5 mt-5">
						<Bar
							data={{
								labels: [
									'Used Products',
									'Products',
									'Registered Farms',
									'Registered Users',
									'Purple',
									'Orange'
								],
								datasets: [
									{
										label: '# of Votes',
										data: [
											dashboardInfo.usedPin,
											dashboardInfo.products,
											dashboardInfo.registeredFarms,
											5,
											2,
											3
										],
										backgroundColor: [
											'rgba(255, 99, 132, 0.2)',
											'rgba(54, 162, 235, 0.2)',
											'rgba(255, 206, 86, 0.2)',
											'rgba(75, 192, 192, 0.2)',
											'rgba(153, 102, 255, 0.2)',
											'rgba(255, 159, 64, 0.2)'
										],
										borderColor: [
											'rgba(255, 99, 132, 1)',
											'rgba(54, 162, 235, 1)',
											'rgba(255, 206, 86, 1)',
											'rgba(75, 192, 192, 1)',
											'rgba(153, 102, 255, 1)',
											'rgba(255, 159, 64, 1)'
										],
										borderWidth: 1
									}
								]
							}}
							options={options}
						/>
					</div>
					<div className="col-md-6">
						<div className="mt-3">
							<p className="">Used Products</p>
							<Line data={data} options={options} />
						</div>
					</div>
					<div className="col-md-6">
						<div className="mt-3">
							<p className="">Users Registration</p>
							<Doughnut data={data} options={options} />
						</div>
					</div>
				</div>
			)}
		</div>
	);
}

export default AdminDashboard;
