import {
  USER_SIGNIN_REQUEST,
  USER_SIGNIN_SUCCESS,
  USER_SIGNIN_FAIL,
  USER_REGISTER_REQUEST,
  USER_REGISTER_SUCCESS,
  USER_REGISTER_FAIL,
  USER_HISTORY_REQUEST,
  USER_HISTORY_SUCCESS,
  USER_HISTORY_FAIL,
  USER_SIGNOUT,
  USER_LOCATION_REQUEST,
  USER_LOCATION_SUCCESS,
  USER_LOCATION_FAIL,
  USER_ADDFARM_REQUEST,
} from "../constants/userConstants";

function userSigninReducer(state = {}, action) {
  switch (action.type) {
    case USER_SIGNIN_REQUEST:
      return { loading: true };
    case USER_SIGNIN_SUCCESS:
      return { loading: false, userInfo: action.payload };
    case USER_SIGNIN_FAIL:
      return { loading: false, error: action.payload };
    case USER_SIGNOUT:
      return {}
    default:
      return state;
  }
}

function userRegisterReducer(state = {}, action) {
  switch (action.type) {
    case USER_REGISTER_REQUEST:
      return { loading: true };
    case USER_REGISTER_SUCCESS:
      return { loading: false, userInfo: action.payload };
    case USER_REGISTER_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
}

function userLocationReducer(state = {}, action) {
	switch (action.type) {
		case USER_LOCATION_REQUEST:
			return { loading: true };
		case USER_LOCATION_SUCCESS:
			return { loading: false, userLocationInfo: action.payload, success: true };
		case USER_LOCATION_FAIL:
			return { loading: false, error: action.payload };
		default:
			return state;
	}
}

function userAddFarmReducer(state = {}, action) {
	switch (action.type) {
		case USER_ADDFARM_REQUEST:
			return { loading: true };
		case USER_ADDFARM_SUCCESS:
			return { loading: false, userLocationInfo: action.payload, success: true };
		case USER_ADDFARM_FAIL:
			return { loading: false, error: action.payload };
		default:
			return state;
	}
}

function userHistoryReducer(state = { histories: [] }, action) {
  switch (action.type) {
    case USER_HISTORY_REQUEST:
      return { loading: true };
    case USER_HISTORY_SUCCESS:
      return { loading: false, histories: action.payload };
    case USER_HISTORY_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
}

export { userSigninReducer, userRegisterReducer, userAddFarmReducer, userLocationReducer, userHistoryReducer };
