/** @format */

import React, { useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import { useDispatch, useSelector } from 'react-redux';
import { dashboardForAdmin } from '../actions/adminActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';

function AdminDashboard(props) {
	const adminDashBoardInfo = useSelector((state) => state.adminDashBoardInfo);
	const { dashboardInfo, loading, error } = adminDashBoardInfo;

	const dispatch = useDispatch();
	useEffect(() => {
		dispatch(dashboardForAdmin());

		return () => {
			//
		};
	}, [dispatch]);

	const options = {
		scales: {
			yAxes: [
				{
					ticks: {
						beginAtZero: true
					}
				}
			]
		}
	};

	return (
		<div className="">
			{loading ? (
				<LoadingBox></LoadingBox>
			) : error ? (
				<MessageBox variant="danger">{error}</MessageBox>
			) : (
				<div className="container-fluid">
					<div className="row mt-5 mb-3">
						<div className="col-lg-3 col-sm-6">
							<div className="info_box_var_1 box_bg_a">
								<div className="info_box_body">
									<span className="info_box_icon icon_group"></span>
									<span className="countUpMe" data-endval="1342">
										{dashboardInfo.registeredUsersToday}
									</span>
								</div>
								<div className="info_box_footer">
									New Users <small>(last 24h)</small>
								</div>
							</div>
						</div>
						<div className="col-lg-3 col-sm-6">
							<div className="info_box_var_1 box_bg_b">
								<div className="info_box_body">
									<span className="info_box_icon icon_cart_alt"></span>
									<span className="countUpMe" data-endval="57">
										57
									</span>
									%
								</div>
								<div className="info_box_footer">Number of Users</div>
							</div>
						</div>
						<div className="col-lg-3 col-sm-6">
							<div className="info_box_var_1 box_bg_c">
								<div className="info_box_body">
									<span className="info_box_icon icon_wallet"></span>$
									<span className="countUpMe" data-endval="13578">
										13 578
									</span>
								</div>
								<div className="info_box_footer">
									Sale
									<small>(last 24h)</small>
								</div>
							</div>
						</div>
						<div className="col-lg-3 col-sm-6">
							<div className="info_box_var_1 box_bg_d">
								<div className="info_box_body">
									<span className="info_box_icon icon_mail_alt"></span>
									<span className="countUpMe" data-endval="531">
										531
									</span>
								</div>
								<div className="info_box_footer">Sent Messages</div>
							</div>
						</div>
					</div>
					<div className="row">
						<div className="col-md-3">
							<p className="text-box">
								Number of Registered Users
								<h3 className="mt-3">{dashboardInfo.registeredUsers}</h3>
							</p>
						</div>
						<div className="col-md-3">
							<p className="text-box">
								Number of Registered Users Today{' '}
								<h3 className="mt-3">{dashboardInfo.registeredUsersToday}</h3>
							</p>
						</div>
						<div className="col-md-3">
							<p className="text-box">
								Number of Unused Products <h3 className="mt-3">{dashboardInfo.products}</h3>
							</p>
						</div>
						<div className="col-md-3">
							<p className="text-box">
								Number of Used Products <h3 className="mt-3">{dashboardInfo.usedPin}</h3>
							</p>
						</div>

						<div className="col-md-6">
							<p className="col-box">
								Number of Registered Farm{' '}
								<h3 className="mt-3">{dashboardInfo.registeredFarms}</h3>
							</p>
						</div>
						<div className="col-md-6">
							<p className="col-box">
								Number of Registered Farms Today{' '}
								<h3 className="mt-3">{dashboardInfo.registeredFarmsToday}</h3>
							</p>
						</div>
					</div>
					<div className="col-md-12 mb-5 mt-5">
						<Bar
							data={{
								labels: [
									'Products',
									'Used Products',
									'Registered Farms',
									'Registered Users',
									'Registered Users Today',
									'Registered Farms Today'
								],
								datasets: [
									{
										label: '# of Items',
										data: [
											dashboardInfo.products,
											dashboardInfo.usedPin,
											dashboardInfo.registeredFarms,
											dashboardInfo.registeredUsers,
											dashboardInfo.registeredUsersToday,
											dashboardInfo.registeredFarmsToday
										],
										backgroundColor: [
											'rgba(255, 99, 132, 0.2)',
											'rgba(54, 162, 235, 0.2)',
											'rgba(255, 206, 86, 0.2)',
											'rgba(75, 192, 192, 0.2)',
											'rgba(153, 102, 255, 0.2)',
											'rgba(255, 159, 64, 0.2)'
										],
										borderColor: [
											'rgba(255, 99, 132, 1)',
											'rgba(54, 162, 235, 1)',
											'rgba(255, 206, 86, 1)',
											'rgba(75, 192, 192, 1)',
											'rgba(153, 102, 255, 1)',
											'rgba(255, 159, 64, 1)'
										],
										borderWidth: 1
									}
								]
							}}
							options={options}
						/>
					</div>
					{/* <div className="col-md-6">
						<div className="mt-3">
							<p className="">Used Products</p>
							<Line
								data={{
									labels: [
										dashboardInfo.groupData[0]['createdAt'],
										dashboardInfo.groupData[1]['createdAt'],
										dashboardInfo.groupData[2]['createdAt']
									],
									datasets: [
										{
											label: '# of Votes',
											data: [
												dashboardInfo.groupData[0]['counts'],
												dashboardInfo.groupData[1]['counts'],
												dashboardInfo.groupData[2]['counts']
											],
											fill: false,
											backgroundColor: 'rgb(255, 99, 132)',
											borderColor: 'rgba(255, 99, 132, 0.2)'
										}
									]
								}}
								options={options}
							/>
						</div>
					</div> */}
				</div>
			)}
		</div>
	);
}

export default AdminDashboard;
