/** @format */

import React, { useState } from 'react';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';

function CropDemo({ src }) {
	const [crop, setCrop] = useState({ unit: 'px', x: 130, y: 50, width: 200, height: 200 });

	const onImageLoaded = (image) => {
		this.setState({ crop: { width: image.width, height: image.height } });
		return false; // Return false when setting crop state in here.
	};

	return (
		<>
			<ReactCrop
				src="https://blog.intercomassets.com/blog/wp-content/uploads/2018/05/Design-leadership-as-a-subversive-activity-.png"
				crop={crop}
				onChange={(newCrop) => setCrop(newCrop)}
				onImageLoaded={onImageLoaded}
			/>
			<img className="" alt src={crop}></img>
		</>
	);
}

export default CropDemo;
