/** @format */

import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { passwordreset } from '../actions/userActions';
import MessageBox from '../components/MessageBox';
import LoadingBox from '../components/LoadingBox';

function PasswordCodeVerify(props) {
	const [code, setCode] = useState('');
	const userPasswordReset = useSelector((state) => state.userPasswordReset);
	const { loading, error, passwordReset } = userPasswordReset;
	const dispatch = useDispatch();

	const submitHandler = (e) => {
		e.preventDefault();
		dispatch(passwordreset(code));
	};

	useEffect(() => {
		if (passwordReset) {
			props.history.push('/password_verify_code');
		}
		return () => {
			//
		};
	}, [passwordReset, props]);
	return (
		<>
			{loading ? (
				<LoadingBox></LoadingBox>
			) : (
				<div className="">
					<div className="container">
						<div className="row">
							<div className="col-md-3"></div>
							<div className="col-md-6">
								<form className="log-blk" onSubmit={submitHandler}>
									<img src="" alt="" />
									<h2 className="text m-2">Verify Password Reset Code</h2>
									<p className="center">
										A reset code has been sent to your registered phone number
									</p>
									{error && <MessageBox variant="danger">{error}</MessageBox>}
									<div className="form-group mt-3">
										<input
											type="text"
											className="form-control"
											id="exampleInputText"
											onChange={(e) => setCode(e.target.value)}
											placeholder="Enter code to verify"
											required
										/>
									</div>

									<button type="submit" className="btn btn-success btn-block">
										Reset Password
									</button>
									<p className="text-center mt-4">
										Have an account? <Link to="/">Log in</Link>
									</p>
								</form>
							</div>
							<div className="col-md-3"></div>
						</div>
					</div>
				</div>
			)}
		</>
	);
}

export default PasswordCodeVerify;
