class QRscanner extends Component {
	constructor(props) {
		super(props);
		this.state = { facingMode: 'user', delay: 500, on: true };
	}

	render() {
		const { selectFacingMode, selectDelay, legacyMode, onAndOff } = this.props;
		const productVerify = useSelector((state) => state.productVerify);
		const { loading, success: successfulVerify, error } = productVerify;

		const dispatch = useDispatch();
		const handleScan = (code) => {
			if (code) {
				dispatch(verify(code));
			}
		};
		const handleError = (err) => {
			console.error(err);
		};

		useEffect(() => {
			if (successfulVerify) {
				let redirect = '/result';
				props.history.push(redirect);
			}
			if (error) {
				let redirect = '/result_fail';
				props.history.push(redirect);
			}
			return () => {
				//
			};
		}, [successfulVerify, error, props.history]);

		return (
			<div style={{ width: '400px', margin: 'auto' }}>
				{onAndOff && (
					<button onClick={() => this.setState({ on: !this.state.on })}>
						{this.state.on ? 'Turn off' : 'Turn on'}
					</button>
				)}
				{selectFacingMode && (
					<select onChange={(e) => this.setState({ facingMode: e.target.value })}>
						<option value="user">User</option>
						<option value="environment">Environment</option>
					</select>
				)}
				{selectDelay && (
					<div>
						<button onClick={() => this.setState({ delay: false })}>Disable Delay</button>
						<input
							placeholder="Delay in ms"
							type="number"
							value={this.state.delay}
							onChange={(e) => this.setState({ delay: parseInt(e.target.value) })}
						/>
					</div>
				)}
				{this.state.on && (
					<QrScan
						onError={handleError}
						onScan={handleScan}
						ref="reader"
						facingMode={this.state.facingMode}
						legacyMode={legacyMode}
						maxImageSize={1000}
						delay={this.state.delay}
						className="reader-container"
					/>
				)}
				{legacyMode && (
					<button onClick={() => this.refs.reader.openImageDialog()}>Open Image Dialog</button>
				)}
			</div>
		);
	}
}

export default QRscanner;
