import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { register } from "../actions/userActions";

function Register(props) {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const userRegister = useSelector((state) => state.userRegister);
  const { loading, userInfo, error } = userRegister;
  const dispatch = useDispatch();
  const redirect = props.location.search
    ? props.location.search.split("=")[1]
    : "/";

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(register(firstName, lastName, phone, password));
  };

  useEffect(() => {
    if (userInfo) {
      props.history.push(redirect);
    }
    return () => {
      //
    };
  }, [userInfo]);

  return (
    <div className="container">
      {loading && (
        <div>
          <div className="text-center h-100 container">
            <img
              src="/Infinity-1.3s-800px.gif"
              className="loader"
              alt="loader"
            />
          </div>
        </div>
      )}
      {error && <div>{error}</div>}
      <div className="row">
        <div className="col-md-3"></div>
        <div className="col-md-6">
          <div className="container card bg-light">
            <article className="card-body ">
              <h4 className="card-title mt-3 text-center">Create Account</h4>
              <p className="text-center">Get started with your free account</p>

              <form onSubmit={submitHandler}>
                <div className="form-group input-group" id="name">
                  <div className="input-group-prepend">
                    <span className="input-group-text">
                      <i className="fa fa-user"></i>
                    </span>
                  </div>
                  <input
                    name=""
                    className="form-control"
                    placeholder="First Name"
                    type="text"
                    onChange={(e) => setFirstName(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group input-group" id="name">
                  <div className="input-group-prepend">
                    <span className="input-group-text">
                      <i className="fa fa-user"></i>
                    </span>
                  </div>
                  <input
                    name=""
                    className="form-control"
                    placeholder="Last Name"
                    type="text"
                    onChange={(e) => setLastName(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group input-group" id="mobileNumber">
                  <div className="input-group-prepend">
                    <span className="input-group-text">
                      <i className="fa fa-phone"></i>
                    </span>
                  </div>
                  <input
                    name=""
                    className="form-control"
                    placeholder="Phone number"
                    type="text"
                    onChange={(e) => setPhone(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group input-group" id="password">
                  <div className="input-group-prepend">
                    <span className="input-group-text">
                      <i className="fa fa-lock"></i>
                    </span>
                  </div>
                  <input
                    className="form-control"
                    placeholder="Create password"
                    type="password"
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <button
                    type="submit"
                    className="btn btn-primary btn-block"
                    onClick={submitHandler}
                  >
                    Create Account
                  </button>
                </div>
                <p className="text-center">
                  Have an account?
                  <Link
                    to={
                      redirect === "/"
                        ? "signin"
                        : "signin?redirect=" + redirect
                    }
                  >
                    Log in
                  </Link>
                </p>
              </form>
            </article>
          </div>
        </div>
        <div className="col-md-3"></div>
      </div>
    </div>
  );
}

export default Register;
